mapper.select:
select m.name as name, a.mem_acc_no, a.acc_num as acc_num, a.bank as bank from member m,
mem_account a
where m.id=a.id and m.id=#{userId}

select m.name as name, c.mem_card_no, c.card_num as card_num, c.card_company as
card_company, c.expire_date from member m, mem_card c
where m.id=c.id and m.id=#{userId}
