mapper.insert:
insert into member (id, password, name, tel, email
, m_img, m_img_addr
)
values (#{id}, #{password}, #{name}, #{tel}, #{email}
, #{m_img}, #{m_img_addr}
)

mapper.update:
update member set name=#{name}, password = #{password}, tel = #{tel}
, m_img = #{m_img}, m_img_addr = #{m_img_addr} 
where id = #{id}
